                    function makeActiveProfile(x) {
                        var y = document.querySelector(".activeProfile");
                        $(y).removeClass("activeProfile");
                        $(y).addClass("passiveProfilePic");
                    }

                    var user = 0;

                    function makeActiveProfile(x) {
                        var y = document.querySelector(".activeProfile");
                        $(y).removeClass("activeProfile");
                        $(y).addClass("passiveProfilePic");

                        let id = x.getAttribute('user');
                        document.getElementById('id').value = id;

                        $.ajax({
                            method: "GET",
                            url: "{{route('getEmployeePersonalDataById')}}",
                            data: {
                                id: id
                            },
                            success: function (data) {
                                document.getElementById('cc_name').innerHTML = data.cc_name;
                                document.getElementById('cc_number').innerHTML = data.cc_number;
                                document.getElementById('name').innerHTML = data.name;
                                document.getElementById('prename').innerHTML = data.prename;
                                document.getElementById('address').innerHTML = data.address;
                                document.getElementById('zip').innerHTML = data.zip;
                                document.getElementById('city').innerHTML = data.city;
                                document.getElementById('job').innerHTML = data.job_position;
                                document.getElementById('fullname').innerHTML = data.name + " " + data.prename;
                                document.getElementById('bank').innerHTML = data.bank;
                                document.getElementById('iban').innerHTML = data.iban;
                            }
                        });
                        $(x).addClass("activeProfile");
                        $(x).removeClass("passiveProfilePic");
                    }

                    function openmodal() {
                        let bcard = $('#bcard').is(':checked');
                        let ibann = $('#ibann').is(':checked');
                        if (bcard && ibann) {
                            $('#cc').show();
                            $('#ibani').show();
                            $('#banka').show();
                            $('#consultmodal').modal('toggle')
                        } else if (bcard) {
                            $('#cc').show();
                            $('#ibani').hide();
                            $('#banka').hide();
                            $('#consultmodal').modal('toggle')

                        } else if (ibann) {
                            $('#ibani').show();
                            $('#banka').show();
                            $('#cc').hide();
                            $('#consultmodal').modal('toggle')
                        }
